Scripts.OnClick:
HideDropDownMenu(1);
BrabbelRP:UISelectCategory(self:GetParent():GetText());
if (IsControlKeyDown()) then
	ModeMenu:Show();
	ToggleDropDownMenu(1, this:GetParent():GetText(), ModeMenu, this, 0, 0);
end

HideDropDownMenu(1);
BrabbelRP:UISelectCategory(self:GetParent():GetText());
if (IsControlKeyDown() and this:GetScript("OnDoubleClick") ~= nil) then
	BrabbelRP:TableDoubleClick(this:GetName(),nil)
end

Scripts.OnKeyDown:
if ( arg1 == "ESCAPE" ) then
	BrabbelRP:ToggleWindowOpen();
elseif ( arg1 == "7" and IsShiftKeyDown()) then
	ChatFrame_OpenChat("/");
elseif ( arg1 == "ENTER" ) then
	ChatFrame_OpenChat("");
elseif ( arg1 == "F2" ) then
	TryToRenameCat();
end
